t_5" data-slide-count="5"><div class="swiper-wrapper"><div class="swiper-slide" style="border-radius:0em"><img src="https://lite-word.com/wp-content/uploads/2025/07/LiteWord_sample_Dental11.png" alt="歯科用3DCT装置"/></div><div class="swiper-slide" style="border-radius:0em"><img src="https://lite-word.com/wp-content/uploads/2025/07/LiteWord_sample_Dental13.png" alt="個室オペ室の写真"/></div><div class="swiper-slide" style="border-radius:0em"><img src="https://lite-word.com/wp-content/uploads/2025/07/LiteWord_sample_Dental12.png" alt="口腔内スキャナでの型取り"/></div><div class="swiper-slide" style="border-radius:0em"><img src="https://lite-word.com/wp-content/uploads/2025/07/LiteWord_sample_Dental14.png" alt="CAD/CAMミリングマシン"/></div><div class="swiper-slide" style="border-radius:0em"><img src="https://lite-word.com/wp-content/uploads/2025/07/LiteWord_sample_Dental15.png" alt="手術後のリカバリールーム"/></div></div></div><script type="text/javascript">
(function(){
	// 全てのギャラリーを初期化
	const initAllGalleries = () => {
		// まだ初期化されていないギャラリーを全て取得
		const galleries = document.querySelectorAll('.paid-block-lw-gallery-3:not([data-swiper-initialized])');
		
		galleries.forEach((wrapper) => {
			const mainEl = wrapper.querySelector('.lw-gallery_images_Swiper_main');
			if (!mainEl || typeof Swiper === 'undefined') return;
			
			// 初期化済みマークを付ける
			wrapper.setAttribute('data-swiper-initialized', 'true');
			
			const slideCount = parseInt(mainEl.dataset.slideCount || '0', 10);
			const mainLoopFlg = slideCount > 1;
			
			let thumbsSwiper = null;
			if (mainLoopFlg) {
				const thumbEl = wrapper.querySelector('.lw-gallery_images_Swiper_sub');
				if (thumbEl) {
					// サムネイルのループは、スライド数が表示数の2倍以上の場合のみ有効
					const thumbSlidesPerView = Math.min(slideCount, 5);
					const thumbLoopFlg = slideCount >= (thumbSlidesPerView * 2);
					
					thumbsSwiper = new Swiper(thumbEl, {
						loop: thumbLoopFlg,
						spaceBetween: 4,
						slidesPerView: thumbSlidesPerView,
						freeMode: true,
						watchSlidesProgress: true
					});
				}
			}
			
			new Swiper(mainEl, {
				loop: mainLoopFlg,
				spaceBetween: 0,
				navigation: mainLoopFlg ? {
					nextEl: wrapper.querySelector('.swiper-button-next'),
					prevEl: wrapper.querySelector('.swiper-button-prev')
				} : {},
				thumbs: mainLoopFlg && thumbsSwiper ? { swiper: thumbsSwiper } : {}
			});
			
			wrapper.classList.remove('init-hide');
		});
	};
	
	// 初期化を確実に実行
	const tryInit = () => {
		if (typeof Swiper !== 'undefined') {
			initAllGalleries();
		} else {
			window.addEventListener('lw:swiperReady', initAllGalleries, { once: true });
		}
	};
	
	// DOMContentLoadedまたは即座に実行
	if (document.readyState === 'loading') {
		document.addEventListener('DOMContentLoaded', tryInit);
	} else {
		tryInit();
	}
})();</script><noscript><style>.paid-block-lw-gallery-3{opacity:1!important}</style></noscript></div>
